package main

import (
	"fmt"
	"strings"
)

func main() {

	//blah := ""
	blah := "mvwvrwvwbblffmvmhmllmzmqzzbhqnnvrnvnmvvqrqrcc"

	var char, str string
	runes := []rune(blah)

	fmt.Print("first letter: ")
	fmt.Println(string(runes[0:4]))

	fmt.Println("start loop")
	for i, b := range runes {

		fmt.Print(i)
		fmt.Print(": ")
		fmt.Print(string(b))
		char = string(b)
		fmt.Print(": ")
		fmt.Println(string(runes[(i + 1):(i + 4)]))
		str = string(runes[(i + 1):(i + 4)])
		fmt.Println(strings.Contains(str, char))

	}

}
