package main

import (
	"fmt"
	"strings"
)

var blah string

// recustion is scary
func dupCheck(str string) bool {

	myRunes := []rune(str)
	myInt := len(str)
	for i, b := range myRunes {

		myChar := string(b)
		myString := string(myRunes[(i + 1):(i + myInt)])

		if !(strings.Contains(myString, myChar)) {
			if myInt == 2 {
				return true
			}
			if dupCheck(myString) {
				return true
			}

		} else {
			return false
		}

	}
	return false
}

func main() {

	blah = ""
	//blah := "qqqqqqqqqqwertyzxcvbuioplkjhmvwvrwvwbblffmvmhmllmzmqzzbhqnnvrnvnmvvqrqrcc"

	var str string
	var end int

	end = (len(blah) - 14)
	runes := []rune(blah)

	fmt.Print("first letter: ")
	fmt.Println(string(runes[0:14]))

	fmt.Println("start loop")
	for i, b := range runes {

		fmt.Println(string(b))
		str = string(runes[(i):(i + 14)])
		fmt.Println(str)

		if dupCheck(str) {

			fmt.Printf("This one: %d", (i + 14))
			fmt.Println(true)
			break
		} else {
			fmt.Println(false)
		}

		if i == end {

			break

		}

	}

}
